package stringPrograms;

public class swapNumbersWithoutThirdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;
		int y=2;
		System.out.println("Before Swaping");
		System.out.println("x="+x+" y="+y);
		x=x+y; 
		y=x-y; 
		x=x-y; 

		System.out.println("Before Swaping");
		System.out.println("x="+x+" y="+y);

	}

}
